import React, { Component, useState, useEffect, useRef, Animated } from "react";
import { Text, StyleSheet, Dimensions, Button, View } from "react-native";
import PropType from "prop-types";
import Svg from "react-native-svg";

export default class BarMarkers extends Component {
  state = {
    barMarkers: [],
  };

  setBarMarkers = () => {
    for (let i = 0; i < 100; i + 20) {
      this.setState({
        barMarkers: [
          ...this.state.barMarkers,
          <View style={styles.bar} margin={widthUnit}></View>,
        ],
      });
    }
  };

  getWidth = () => {
    const windowWidth = Dimensions.get("window");
    return windowWidth.width;
  };

  render() {
    const windowWidth = Dimensions.get("window");
    const widthUnit = windowWidth.width / 72;
    this.setBarMarkers;
    console.log(this.state.barMarkers);

    return <View style={styles.barContainer}>{this.state.barMarkers}</View>;
  }
}

const styles = StyleSheet.create({
  barContainer: {
    marginTop: 140,
    width: getWidth(),
    height: 40,
    flexDirection: "row",
    backgroundColor: "#DDDD",
  },
  bar: {
    width: 1,
    flexDirection: "column",
    backgroundColor: "black",
  },
});
