import React, { Component, useState, useEffect, useRef, Animated } from "react";
import { Text, StyleSheet, Dimensions, Button, View } from "react-native";
import PropType from "prop-types";
import Svg from "react-native-svg";

export default class BarMarkers extends Component {
  static propTypes = {
    barMarkers: PropType.array.isRequired,
  };

  setBarMarkers = (x, y) => {
    for (let i = 0; i <= x; i + y) {
      const newMarker = (
        <Line x1={i} y1="0" x2={i} y2="100" stroke="red" strokeWidth="1" />
      );

      this.props.barMarkers = this.props.barMarkers.concat(newMarker);
    }

    console.log(this.props.barMarkers);
  };

  render() {
    const windowWidth = Dimensions.get("window");
    const widthUnit = windowWidth.width / 72;
    this.setBarMarkers(windowWidth, widthUnit);
    return (
      <View style={styles.container}>
        <Svg>{this.props.barMarkers}</Svg>
      </View>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    height: "100%",
    flex: 1,
    flexDirection: "coloumn",
    backgroundColor: "blue",
    justifyContent: "space-around",
  },
});
