    const { step } = this.state;
    console.log(step);
    return (
      <View marginTop={120}>
        <Text style={{ textAlign: "center" }}>Progress...</Text>
        <View style={styles.progressContainer}>
          <View style={styles.progress} width={`S{step}%`}></View>
        </View>
        <Button
          title="Increment Progress"
          onPress={this.handleIncrease}
        ></Button>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  progressContainer: {
    marginVertical: 20,
    borderWidth: 0.5,
    height: 20,
    borderRadius: 20,
    overflow: "hidden",
