import React, { Component } from "react";
import { View, StyleSheet, Text, Dimensions } from "react-native";
import { Line, Svg } from "react-native-svg";

import ProgressBar from "./ProgressBar";

export default class Index extends Component {
  state = {
    barMarkers: [],
  };

  setBarMarkers = (barSeparation) => {
    for (let i = 0; i < 100; i + barSeparation) {
      this.setState({
        barMarkers: [
          ...this.state.barMarkers,
          <Line x1={i} y1="0" x2={i} y2="100" stroke=" red" strokeWidth="2" />,
        ],
      });
    }
  };

  componentDidMount() {
    const windowWidth = Dimensions.get("window");
    const widthUnit = Dimensions.get("window") / 72;
    this.setBarMarkers(widthUnit);
  }

  render() {
    // console.log(barMarkers);
    return (
      <View style={styles.container}>
        <ProgressBar></ProgressBar>
        <View flex={1} flexDirection="coloumn" justifyContents="space-around">
          <Svg height="100" width="100">
            {this.state.barMarkers.map((x) => x)}
          </Svg>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "black",
  },
});
