import React, { Component } from "react";
import { View, StyleSheet, Text, Dimensions } from "react-native";
import { Line, Svg } from "react-native-svg";

import ProgressBar from "./ProgressBar";

export default class Index extends Component {
  state = {
    barMarkers: [],
  };
  componentDidMount() {
    // for (let i = 0; i <= 100; i + 50) {
    const newMarker = (
      <Line x1={20} y1="0" x2={20} y2="100" stroke="red" strokeWidth="1" />
    );
    const newMarker2 = (
      <Line x1={40} y1="0" x2={40} y2="100" stroke="black" strokeWidth="1" />
    );
    this.setState({
      barMarkers: this.state.barMarkers.concat(newMarker),
    });
  }
  setBarMarkers = () => {
    // }
  };

  render() {
    const windowWidth = Dimensions.get("window");
    const widthUnit = windowWidth.width / 72;
    console.log(widthUnit);
    console.log(barMarkers);
    return (
      <View style={styles.container}>
        <ProgressBar></ProgressBar>
        <View flex={1} flexDirection="coloumn" justifyContents="space-between">
          <Svg height="100" width="100">
            {this.state.barMarkers.map((x) => x)}
          </Svg>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "blue",
  },
});
